    private void btnIngresar1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnIngresar1ActionPerformed
        if (abb.VerificarCedula(txtCedula.getText()) == true) {
            JOptionPane.showMessageDialog(null, "Cedula Valida ");
            String sql;
            String mensaje = "";
            reg = abb.conexion();
            try {
                personaVotante = new PersonaVotante(txtCedula.getText(), txtNombre1.getText(), (cbProvincia.getSelectedItem() + ""), (cbCanton.getSelectedItem() + ""), (cbParroquia.getSelectedItem() + ""), txtRecinto1.getText(), Integer.parseInt(txtMesa1.getText()), txtFechaNacimiento1.getText(), (cbGenero1.getSelectedItem() + ""));
                if (cbProvincia.getSelectedIndex() >= 0 && cbCanton.getSelectedIndex() >= 0 && cbParroquia.getSelectedIndex() >= 0) {
                String ced = txtCedula.getText();
                String nom = txtNombre1.getText();
                String cP = ((Provincia) cbProvincia.getSelectedItem()).getCodigo();
                String cC = ((Canton) cbCanton.getSelectedItem()).getCodigo();
                String cPa = ((Parroquia) cbParroquia.getSelectedItem()).getCodigo();
                String reci = txtRecinto1.getText();
                String mesa = txtMesa1.getText();
                String Fech = txtFechaNacimiento1.getText();
                String gen = (cbGenero1.getSelectedItem() + "");

                sql = "INSERT INTO persona(cedula,nombre,codProvincia,codCanton,codParroquia,recinto,mesa,fecha_nacimiento,genero)VALUES (?,?,?,?,?,?,?,?,?)";
                try {
                    PreparedStatement pst = reg.prepareStatement(sql);
                    pst.setString(1, ced);
                    pst.setString(2, nom);
                    pst.setString(3, cP);
                    pst.setString(4, cC);
                    pst.setString(5, cPa);
                    pst.setString(6, reci);
                    pst.setString(7, mesa);
                    pst.setString(8, Fech);
                    pst.setString(9, gen);
                    int n = pst.executeUpdate();
                    if (n > 0) {
                        mensaje = "SI";
                        JOptionPane.showMessageDialog(null, "registrado con exito");
                    }

                } catch (SQLException ex) {
                    mensaje = "NO";
                    Logger.getLogger(FrmPrincipal.class.getName()).log(Level.SEVERE, null, ex);
                }
            }

            } catch (NumberFormatException e) {
                JOptionPane.showMessageDialog(null, "revise que los datos sean correctos");
            }
            cbProvincia.setSelectedIndex(0);
            txtMesa1.setText("");
            txtRecinto1.setText("");
            cbGenero1.setSelectedIndex(0);
            txtFechaNacimiento1.setText("");
            txtNombre1.setText("");
            cbParroquia.setSelectedIndex(0);
            cbCanton.setSelectedIndex(0);
            txtCedula.setText("");
            jFrame1.setVisible(false);
            
            

        } else {
            JOptionPane.showMessageDialog(null, "cedula Invalida ");
        }

    }//GEN-LAST:event_btnIngresar1ActionPerformed

