    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        reg = abb.conexion();
        try {
            PreparedStatement pst = reg.prepareStatement("SELECT * FROM provincia");
            ResultSet provincias = pst.executeQuery();
            while (provincias.next()) {
                Provincia provincia = new Provincia();
                provincia.setCodigo(provincias.getNString("cod_Provincia"));
                provincia.setNombre(provincias.getNString("provincia"));
                cbProvincia.addItem(provincia);
            }
            provincias.close();
        } catch (SQLException ex) {
            Logger.getLogger(FrmPrincipal.class.getName()).log(Level.SEVERE, null, ex);
        }



    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        try {
            reg = abb.conexion();
            PreparedStatement pst = reg.prepareStatement("select cedula, nombre, pro.provincia as provincia, c.canton as canton, pa.parroquia as parroquia, recinto, mesa, fecha_nacimiento, genero "
                    + "from persona p, provincia pro, canton c, parroquia pa "
                    + "where p.codProvincia=pro.cod_Provincia and c.cod_Canton=p.codCanton and pa.cod_Parroquia=p.codParroquia;");
            ResultSet resultado = pst.executeQuery();
            while (resultado.next()) {
                personaVotante = new PersonaVotante(resultado.getNString("cedula"), resultado.getNString("nombre"), resultado.getNString("provincia"), resultado.getNString("canton"), resultado.getNString("parroquia"), resultado.getNString("recinto"), Integer.parseInt(resultado.getNString("mesa")), resultado.getNString("fecha_nacimiento"), resultado.getNString("genero"));
                abb.ingresar(personaVotante);

            }
            resultado.close();

        } catch (SQLException ex) {
            Logger.getLogger(FrmPrincipal.class.getName()).log(Level.SEVERE, null, ex);
        }

    }//GEN-LAST:event_jButton2ActionPerformed

    private void cbProvinciaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cbProvinciaActionPerformed
        cbCanton.removeAllItems();
        if (cbProvincia.getSelectedIndex() >= 0) {
            Provincia provincia = (Provincia) cbProvincia.getSelectedItem();
            try {
                PreparedStatement pst = reg.prepareStatement("SELECT * FROM canton where cod_Provincia='" + provincia.getCodigo() + "';");
                ResultSet cantones = pst.executeQuery();

                while (cantones.next()) {
                    Canton canton = new Canton();
                    canton.setCodigo(cantones.getNString("cod_Canton"));
                    canton.setNombre(cantones.getNString("canton"));
                    cbCanton.addItem(canton);
                }
                cantones.close();
            } catch (SQLException ex) {
                Logger.getLogger(FrmPrincipal.class.getName()).log(Level.SEVERE, null, ex);
            }
        }
    }//GEN-LAST:event_cbProvinciaActionPerformed

