    private void cbCantonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cbCantonActionPerformed
        cbParroquia.removeAllItems();
        if (cbCanton.getSelectedIndex() >= 0) {
            Canton canton = (Canton) cbCanton.getSelectedItem();
            try {
                PreparedStatement pst = reg.prepareStatement("SELECT * FROM parroquia where cod_Canton='" + canton.getCodigo() + "';");
                ResultSet parroquias = pst.executeQuery();
                while (parroquias.next()) {
                    Parroquia parroquia = new Parroquia();
                    parroquia.setCodigo(parroquias.getNString("cod_Parroquia"));
                    parroquia.setNombre(parroquias.getNString("parroquia"));
                    cbParroquia.addItem(parroquia);
                }
                parroquias.close();
            } catch (SQLException ex) {
                Logger.getLogger(FrmPrincipal.class.getName()).log(Level.SEVERE, null, ex);
            }
        }
    }//GEN-LAST:event_cbCantonActionPerformed

